components/common/navigation-list:
{{#if children}}
<a class="navPages-action has-subMenu {{#if theme_settings.subcategories_menu_vertical}}navPages-action--vertical{{/if}}" href="{{url}}"
    {{#if id '==' category.id}}data-current-category{{/if}}
    data-collapsible="navPages-{{id}}{{idSuffix}}"
    {{#unless theme_settings.subcategories_menu_vertical}}data-collapsible-disabled-breakpoint="medium"{{/unless}}
    data-collapsible-disabled-state="open"
    data-collapsible-enabled-state="closed">
    {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
</a>
<div class="navPage-subMenu
        {{#if theme_settings.subcategories_menu_vertical}}
            navPage-subMenu--vertical
        {{else if theme_settings.navPages_standard}}
            navPage-subMenu--standard
        {{/if}}
    " id="navPages-{{id}}{{idSuffix}}" aria-hidden="true" tabindex="-1">
    <ul class="navPage-subMenu-list">
        <li class="navPage-subMenu-item">
            <a class="navPage-subMenu-action navPages-action" href="{{url}}">{{lang 'category.view_all.name' category=name}}</a>
        </li>
        {{#each children}}
            <li class="navPage-subMenu-item">
                {{#if children}}
                    <a
                        class="navPage-subMenu-action navPages-action has-subMenu"
                        href="{{url}}"
                        {{#if id '==' ../../category.id}}data-current-category{{/if}}
                        data-collapsible="navPages-{{id}}{{../../idSuffix}}"
                        {{#unless ../../theme_settings.subcategories_menu_vertical}}data-collapsible-disabled-breakpoint="medium"{{/unless}}
                        data-collapsible-disabled-state="open"
                        data-collapsible-enabled-state="closed">
                        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
                    </a>
                    <ul class="navPage-childList" id="navPages-{{id}}{{../../idSuffix}}">
                        <li class="navPage-childList-item">
                            <a class="navPage-childList-action navPages-action" href="{{url}}">{{lang 'category.view_all.name' category=name}}</a>
                        </li>
                        {{#each children}}
                        <li class="navPage-childList-item">
                            {{> components/common/navigation-list-child 
                                    category_id=../../../category.id 
                                    idSuffix=../../../idSuffix 
                                    theme_settings=../../../theme_settings}}
                        </li>
                        {{/each}}
                    </ul>
                {{else}}
                    <a class="navPage-subMenu-action navPages-action" href="{{url}}" {{#if id '==' ../../category.id}}data-current-category{{/if}}>{{name}}</a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
</div>
{{else}}
<a class="navPages-action" href="{{url}}" {{#if id '==' category.id}}data-current-category{{/if}}>{{name}}</a>
{{/if}}

components/common/share:
{{#if settings.add_this.buttons}}
    <div class="addthis_toolbox addthis_32x32_style" addthis:url="{{url}}"
         addthis:title="{{title}}">
        <ul class="socialLinks socialLinks--alt">
            {{#each settings.add_this.buttons}}
                <li class="socialLinks-item socialLinks-item--{{service}}">
                    <a class="{{#if service '!==' 'facebook_like'}}addthis_button_{{service}} {{/if}}icon icon--{{service}}" {{{annotation}}}>
                        {{#if service '===' 'google_plusone'}}
                            <!-- No custom icons allowed with g+ -->
                        {{/if}}

                        {{#if service '===' 'facebook'}}
                            <svg>
                                <use xlink:href="#icon-facebook"/>
                            </svg>
                        {{/if}}

                        {{#if service '===' 'email'}}
                            <svg>
                                <use xlink:href="#icon-envelope"/>
                            </svg>
                        {{/if}}

                        {{#if service '===' 'print'}}
                            <svg>
                                <use xlink:href="#icon-print"/>
                            </svg>
                        {{/if}}

                        {{#if service '===' 'twitter'}}
                            <svg>
                                <use xlink:href="#icon-twitter"/>
                            </svg>
                        {{/if}}

                        {{#if service '===' 'linkedin'}}
                            <svg>
                                <use xlink:href="#icon-linkedin"/>
                            </svg>
                        {{/if}}

                        {{#if service '===' 'google'}}
                            <svg>
                                <use xlink:href="#icon-google"/>
                            </svg>
                        {{/if}}

                        {{#if service '===' 'pinterest'}}
                            <svg>
                                <use xlink:href="#icon-pinterest"/>
                            </svg>
                        {{/if}}

                        {{#if service '===' 'facebook_like'}}
                            <iframe src="https://www.facebook.com/plugins/like.php?href={{#if ../../product}}{{encodeURI ../../../product.url}}{{else}}{{encodeURI ../../../post.url}}{{/if}}&amp;width=79&amp;layout=button_count&amp;action=like&amp;size=small&amp;show_faces=false&amp;share=false&amp;height=21&amp;appId=140586622674265" width="79" height="21" style="border:none;overflow:hidden" scrolling="no" frameborder="0" allowTransparency="true" allow="encrypted-media"></iframe>
                        {{/if}}
                    </a>
                </li>
            {{/each}}
        </ul>
        <script type="text/javascript"
                src="//s7.addthis.com/js/300/addthis_widget.js#pubid=ra-4e94ed470ee51e32"></script>
        <script>
            if (typeof(addthis) === "object") {
                addthis.toolbox('.addthis_toolbox');
            }
        </script>
    </div>
{{/if}}

components/papa-supermarket/sidebar/blog:
{{#if posts}}
<div class="sidebarBlock sidebarBlock--blog">
    <h5 class="sidebarBlock-heading" data-collapsible="#sidebar-blog-navList" data-collapsible-disabled-breakpoint="medium" aria-expanded="false">{{lang 'blog.recent_posts'}}</h5>
    <ul class="navList">
        {{#each posts}}
        <li class="navList-item">
            {{> components/blog/post post=this hide_social_icons=true settings=../settings hide_summary=../hide_summary hide_readmore=../hide_readmore}}
        </li>
        {{/each}}
    </ul>
</div>
{{/if}}

