    {{#if id '==' category.id}}data-current-category{{/if}}
    data-collapsible="navPages-{{id}}{{idSuffix}}"
    {{#unless theme_settings.subcategories_menu_vertical}}data-collapsible-disabled-breakpoint="medium"{{/unless}}
    data-collapsible-disabled-state="open"
    data-collapsible-enabled-state="closed">
    {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
</a>
<div class="navPage-subMenu
        {{#if theme_settings.subcategories_menu_vertical}}
            navPage-subMenu--vertical
        {{else if theme_settings.navPages_standard}}
            navPage-subMenu--standard
        {{/if}}
    " id="navPages-{{id}}{{idSuffix}}" aria-hidden="true" tabindex="-1">
    <ul class="navPage-subMenu-list">
        <li class="navPage-subMenu-item">
            <a class="navPage-subMenu-action navPages-action" href="{{url}}">{{lang 'category.view_all.name' category=name}}</a>
        </li>
        {{#each children}}
            <li class="navPage-subMenu-item">
                {{#if children}}
                    <a
                        class="navPage-subMenu-action navPages-action has-subMenu"
                        href="{{url}}"
                        {{#if id '==' ../../category.id}}data-current-category{{/if}}
                        data-collapsible="navPages-{{id}}{{../../idSuffix}}"
                        {{#unless ../../theme_settings.subcategories_menu_vertical}}data-collapsible-disabled-breakpoint="medium"{{/unless}}
                        data-collapsible-disabled-state="open"
                        data-collapsible-enabled-state="closed">
                        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
                    </a>
                    <ul class="navPage-childList" id="navPages-{{id}}{{../../idSuffix}}">
                        <li class="navPage-childList-item">
                            <a class="navPage-childList-action navPages-action" href="{{url}}">{{lang 'category.view_all.name' category=name}}</a>
                        </li>
                        {{#each children}}
                        <li class="navPage-childList-item">
                            {{> components/common/navigation-list-child 
                                    category_id=../../../category.id 
                                    idSuffix=../../../idSuffix 
                                    theme_settings=../../../theme_settings}}
                        </li>
                        {{/each}}
                    </ul>
                {{else}}
                    <a class="navPage-subMenu-action navPages-action" href="{{url}}" {{#if id '==' ../../category.id}}data-current-category{{/if}}>{{name}}</a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
</div>
{{else}}
<a class="navPages-action" href="{{url}}" {{#if id '==' category.id}}data-current-category{{/if}}>{{name}}</a>
{{/if}}

